// This file is automatically compiled by Webpack, along with any other files
// present in this directory. You're encouraged to place your actual application logic in
// a relevant structure within app/javascript and only use these pack files to reference
// that code so it'll be compiled.

require("@rails/ujs").start()
require("@rails/activestorage").start()
require("channels")
// require("jquery")
import JQuery from 'jquery'
// import 'bootstrap';
import '@fortawesome/fontawesome-free/js/all';
import '@fortawesome/fontawesome-free/css/all';

window.$ = window.JQuery = JQuery;
var accordionShow = false
$(document).ready(function () {
    setTimeout(function() {
        $('.alert-text').fadeOut();
    }, 4000);

    $('.checkbox').on("click", function() {
        console.log("clicked");
        if($(this).hasClass('unchecked')) {
            $(this).removeClass();
            $(this).addClass('checkbox checked fas fa-check-square fa-lg');
            console.log("class of checkbox is " + $(this).attr('class'));
        } else {
            $(this).removeClass();
            $(this).addClass('checkbox unchecked far fa-square fa-lg');
            console.log("class of checkbox is " + $(this).attr('class'));
        }
    });

    $('#documentButton').click(function() {
        var collapseId = '#collapse' + $(this).parents('.folder').attr('id')
        if ($(collapseId).hasClass('show')) {
            accordionShow = true;
        }
    });
});
